import { Injectable, OnInit } from '@angular/core';
import { Tag } from 'src/app/shared/models/Tag';
import { FoodService } from '../food/food.service';
import { Food } from 'src/app/shared/models/food';

@Injectable({
  providedIn: 'root'
})
export class TagsService implements OnInit{

  constructor(private foodService : FoodService) { }
  foodItems:Food[] = [];
  tagMap ={};

  ngOnInit(): void {
    this.getFoodItems();
  }
  async getFoodItems(){
    await this.foodService.getAllFoodItemsSync().then((resp)=>{
      this.foodItems=resp;
    })

  }
  getAllTags() : Tag[]{
    // Admins will allow some mostly searched tags: eg show the mostly searched 5 tags
    // Solely on the discretionary of the admins:
    for(let food of this.foodItems){
      for(let tag of this.foodItems['tags']){
        this.tagMap[tag.toLowerCase()]++;
      }
    }
    console.log(this.tagMap);

    return [
      { name: 'All', count: 8 },
      { name: 'NonVeg', count: 2 },
      { name: 'Veg', count: 2 },
      { name: 'South Indian', count: 1 },
      { name: 'Birthday', count: 2 }
    ];
  }
}
