import { Injectable, OnInit } from '@angular/core';
import { Food } from 'src/app/shared/models/food';
import { Tag } from 'src/app/shared/models/Tag';
import { ApiService } from '../api/api.service';
@Injectable({
  providedIn: 'root'
})
export class FoodService implements OnInit{

  ngOnInit() : void{
    this.getAllFoodItems();
  }
  constructor(private apiService : ApiService) { }
  foodList:Food[]=[]

  async getAllFoodItemsSync(){
    let data = await this.apiService.getAllFoodItems().then((resp)=>{
      this.foodList=resp;
    });
    console.log(this.foodList);
    // data=await data.json();
    // this.foodList=data;
    // console.log(this.foodList);
    return data;
  }

  async getAllFoodItems(){
    await this.getAllFoodItemsSync();
    return this.foodList;
  }
  getFoodById(id: number): Food {
    return this.foodList.find(food => food.id == id)!;
  }
  getAllFoodByTag(tag: string): Food[] {
    return tag == 'All' ? this.foodList : this.foodList.filter(food => food.tags.includes(tag.toLowerCase().trim()));
  }
  getAllTag(): Tag[] {
    // REST API Call
    return [
      { name: 'All', count: 8 },
      { name: 'NonVeg', count: 2 },
      { name: 'Veg', count: 2 },
      { name: 'South Indian', count: 1 },
      { name: 'Birthday', count: 2 }
    ];
  }

}
